mapper.select:
SELECT NVL(SUM(oPrice*oQty),0) money
FROM ORDERS O , ACTIVITY A
WHERE o.actId = a.actId AND o.oNo=#{oNo}

mapper.update:
UPDATE ORDERS
SET oQty = #{oQty}
WHERE mId = #{mId} AND actId = #{actId}

mapper.select:
SELECT COUNT(*)
FROM ORDERS
WHERE mId = #{mId} AND actId = #{actId}

mapper.update:
UPDATE ODERS
SET oQty = oQty+#{oQty}
WHERE mId = #{mId} AND 	actId = #{actId}
